package assignment1;

public class AsciiValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Ascii Value Conversion from Char to Int
		char a = 'B';
		int b = (int)a;
		 
		System.out.println("ASCII Value: "+b);
		
		//int to char;
		int c = 66;
		char d = (char)c;
		
		System.out.println("Character: "+d);
	}

}
