package assignment1;

public class ImplicitCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//implicit casting
		byte a = 12;
		int b = a;
		double c = b;
		
		//byte
		System.out.println(a);
		//byte to int
		System.out.println(b);
		//int to double
		System.out.println(c);
	}

}
